	public void setLstTeams(ArrayList<Team> lstTeams) {
		this.allTeams = lstTeams;
	}
	
	public Team getTeam(int id) {
		Team team =new Team();
		try {
			DBConnection dbConnection =new DBConnection();
			Connection conn =dbConnection.getConnection();
			String sql ="SELECT * FROM team WHERE id =?";
			PreparedStatement statement =conn.prepareStatement(sql);
			statement.setInt(1, id);
			ResultSet rs =statement.executeQuery();
			while(rs.next()) {
				team.setName(rs.getString("Name"));
				team.setId(rs.getInt("id"));
			}
		}catch (Exception e) {
			System.out.println("TeamtService-> getTeam():"+e.getMessage());
			team = null;
		}
		
		return team;
		
	}
	public boolean insertTeam(Team team) {
		boolean inSuccess=false;
		
		try {
			DBConnection dbConnection= new DBConnection();
			Connection conn = dbConnection.getConnection();
			String sql ="INSERT INTO team(name)VALUES(?)";
			PreparedStatement statement = conn.prepareStatement(sql);
			statement.setString(1,team.getName());
			int effectedRow=statement.executeUpdate();
			if(effectedRow!=0)
				inSuccess=true;
			else 
				inSuccess=false;
	    
			} catch (Exception e) {
			System.out.println("TeamService->insertTeam() :"+e.getMessage());
			inSuccess=false;
		}
		
		return inSuccess;

	}
	public boolean deleteTeam(int id) {
		boolean isSuccess= true;
		try {
			DBConnection dbConnection =new DBConnection();
			Connection conn =dbConnection.getConnection();
			String sql ="DELETE FROM team WHERE id =?";
			PreparedStatement statement = conn.prepareStatement(sql);
			statement.setInt(1, id);
			int effectedRow = statement.executeUpdate();
			
			if(effectedRow!=0) {
				isSuccess =true;
			}
			else {
				isSuccess =false;
			}
		}catch (Exception e) { 
			System.out.println("TeamService-> deleteTeam():"+e.getMessage());
			isSuccess =false;
	}
		return isSuccess;	
	}
	public boolean updateTeam (Team team) {
		boolean isSuccess =false;
		try {
			DBConnection dbConnection =new DBConnection();
			Connection conn =dbConnection.getConnection();
			String sql ="UPDATE team SET name=? WHERE id =?";
			PreparedStatement statement = conn.prepareStatement(sql);
			statement.setString(1, team.getName());
			statement.setInt(2, team.getId());
			int effectedRow = statement.executeUpdate();
			
			if(effectedRow!=0)
				isSuccess =true;
			else {
				isSuccess =false;
			}
		}catch (Exception e) { 
			System.out.println("TeamService-> updateTeam():"+e.getMessage());
			isSuccess =false;
			
		}
		return isSuccess;
	}
	public ArrayList<Team> getAllTeams() {
		try {
			DBConnection dbConnection =new DBConnection();
			Connection conn =dbConnection.getConnection();
			String sql ="SELECT * FROM team";
			Statement statement =conn.createStatement();
			ResultSet rs= statement.executeQuery(sql);
			while(rs.next()) {
				//process
				Team t =new Team();
				t.setId(rs.getInt("id"));
				t.setName(rs.getString("name"));
				                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        
				OrderService service = new OrderService();
				ArrayList<Order> orders = service.getOrdersFromTeamId(rs.getInt("id"));
				t.setOrders(orders);
				allTeams.add(t); 
			}
				rs.close();
				statement.close();
				conn.close();
				
		} catch (Exception e) {
			allTeams = null;
			System.out.println("TeamService-> getAllTeams():"+e.getMessage());
		}
		
		return allTeams;
	}
